import React, { useState } from "react";

function Portfolio() {
  const [filter, setFilter] = useState("all");

  const projects = [
    {
      id: 1,
      title: "BookshelfApp",
      category: "web",
      image: "/projects/portfolio.jpg",
      description: "Website CRUD Sederhana",
      technologies: ["HTML", "CSS", "JavaScript"],
      link: "https://yosparpur.github.io/bookshelf-app-starter-project/",
    },
    // Tambahkan proyek lain di sini
  ];

  return (
    <article className="portfolio-section page-transition">
      <div className="section-header">
        <h2>Portfolio</h2>
        <div className="title-bar"></div>
      </div>

      <div className="portfolio-filter">
        <button
          className={`filter-btn ${filter === "all" ? "active" : ""}`}
          onClick={() => setFilter("all")}
        >
          All
        </button>
        <button
          className={`filter-btn ${filter === "web" ? "active" : ""}`}
          onClick={() => setFilter("web")}
        >
          Web
        </button>
        <button
          className={`filter-btn ${filter === "dataanalyst" ? "active" : ""}`}
          onClick={() => setFilter("dataanalyst")}
        >
          Data Analyst
        </button>
      </div>

      <div className="portfolio-grid">
        {projects
          .filter((project) => filter === "all" || project.category === filter)
          .map((project) => (
            <div key={project.id} className="portfolio-item">
              <div className="portfolio-img-wrapper">
                <img src={project.image} alt={project.title} />
                <div className="portfolio-overlay">
                  <div className="portfolio-info">
                    <h4>{project.title}</h4>
                    <div className="tech-stack">
                      {project.technologies.map((tech) => (
                        <span key={tech} className="tech-tag">
                          {tech}
                        </span>
                      ))}
                    </div>
                    <a
                      href={project.link}
                      target="_blank"
                      rel="noopener noreferrer"
                      className="view-project"
                    >
                      <i className="fas fa-external-link-alt"></i> View Project
                    </a>
                  </div>
                </div>
              </div>
              <div className="portfolio-content">
                <h4>{project.title}</h4>
                <p>{project.description}</p>
              </div>
            </div>
          ))}
      </div>
    </article>
  );
}

export default Portfolio;
